import React, { Component } from "react";
import "./style.css";
import API from "../utils/API";
import Buttons from "../Buttons/index";
import Delete from "../Delete/index";

class ProductGrid extends Component {
  constructor(props) {
    super(props);

    this.state = {
      product: [],
    };
  }

  componentDidMount() {
    this.loadProducts();
  }

  loadProducts = () => {
    API.getProducts().then((res) => {
      const products = res.data.map((data) => ({
        image: data.image,
        title: data.title,
        description: data.description,
        quantity: data.quantity,
        price: data.price,
        id: data.id,
      }));
      this.setState({
        product: products,
      });
    });
  };

  deleteProduct = (id) => {
    console.log(id);
    API.deleteProduct(id)

      .then((res) => this.loadProducts())
      .catch((err) => console.log(err));
  };

  sortTable = (n) => {
    let table,
      rows,
      switching,
      i,
      x,
      y,
      shouldSwitch,
      dir,
      switchcount = 0;
    table = document.getElementById("pGrid");
    switching = true;
    dir = "asc";
    while (switching) {
      switching = false;
      rows = table.rows;
      for (i = 1; i < rows.length - 1; i++) {
        shouldSwitch = false;
        x = rows[i].getElementsByTagName("TD")[n];
        y = rows[i + 1].getElementsByTagName("TD")[n];

        if (dir === "asc") {
          if (x.innerHTML.toLowerCase() > y.innerHTML.toLowerCase()) {
            shouldSwitch = true;
            break;
          }
        } else if (dir === "desc") {
          if (x.innerHTML.toLowerCase() < y.innerHTML.toLowerCase()) {
            shouldSwitch = true;
            break;
          }
        }
      }
      if (shouldSwitch) {
        rows[i].parentNode.insertBefore(rows[i + 1], rows[i]);
        switching = true;
        switchcount++;
      } else {
        if (switchcount === 0 && dir === "asc") {
          dir = "desc";
          switching = true;
        }
      }
    }
  };

  render() {
    return (
      <div className=" container main my-5">
        <div className="row">
          <div className="col-8">
            <h3>Products</h3>
          </div>
          <div className="col-4 float-right">
            <Buttons />
          </div>
        </div>
        <div className="row">
          <table id="pGrid" className="table table-striped">
            <thead className="thead-dark">
              <tr>
                <th onClick={this.sortTable(0)} scope="col">
                  ID
                </th>
                <th scope="col">Image</th>
                <th onClick={this.sortTable(1)} scope="col">
                  Product Name
                </th>
                <th scope="col">Description</th>
                <th onClick={this.sortTable(4)} scope="col">
                  Quantity
                </th>
                <th onClick={this.sortTable(3)} scope="col">
                  Price
                </th>
                <th scope="col">DELETE</th>
              </tr>
            </thead>
            <tbody>
              {this.state.product.map((data) => (
                <tr key={data.id}>
                  <td>{data.id}</td>
                  <td>
                    <img src={data.image} alt="productImage"></img>
                  </td>
                  <td className="title">{data.title}</td>
                  <td>{data.description}</td>
                  <td>{data.quantity}</td>
                  <td>${data.price}</td>
                  <td>
                    <Delete onClick={() => this.deleteProduct(data.id)} />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ProductGrid;
