import React, { Component } from "react";
import "./style.css";
import API from "../utils/API";
import Buttons from "../Buttons/index";
import Delete from "../Delete/index";

class ProductGrid extends Component {
  constructor(props) {
    super(props);

    this.state = {
      product: [],
    };
  }

  componentDidMount() {
    this.loadProducts();
  }

  loadProducts = () => {
    API.getProducts().then((res) => {
      const products = res.data.map((data) => ({
        image: data.image,
        title: data.title,
        description: data.description,
        quantity: data.quantity,
        price: data.price,
        id: data.id,
      }));
      this.setState({
        product: products,
      });
    });
  };

  deleteProduct = (id) => {
    console.log(id);
    API.deleteProduct(id)

      .then((res) => this.loadProducts())
      .catch((err) => console.log(err));
  };

  useSortableData = (items, config = null) => {
    const [sortConfig, setSortConfig] = React.useState(config);

    const sortedItems = React.useMemo(() => {
      let sortableItems = [...items];
      if (sortConfig !== null) {
        sortableItems.sort((a, b) => {
          if (a[sortConfig.key] < b[sortConfig.key]) {
            return sortConfig.direction === 'ascending' ? -1 : 1;
          }
          if (a[sortConfig.key] > b[sortConfig.key]) {
            return sortConfig.direction === 'ascending' ? 1 : -1;
          }
          return 0;
        });
      }
      return sortableItems;
    }, [items, sortConfig]);

    const requestSort = (key) => {
      let direction = 'ascending';
      if (
        sortConfig &&
        sortConfig.key === key &&
        sortConfig.direction === 'ascending'
      ) {
        direction = 'descending';
      }
      setSortConfig({ key, direction });
    };

    return { items: sortedItems, requestSort, sortConfig };
  };

//   ProductTable = (props) => {
//     const { items, requestSort, sortConfig } = useSortableData(props.products);
//     const getClassNamesFor = (name) => {
//       if (!sortConfig) {
//         return;
//       }
//       return sortConfig.key === name ? sortConfig.direction : undefined;
//     };
// }   

  render() {
    return (
      <div className=" container main my-5">
        <div className="row">
          <div className="col-8">
            <h3>Products</h3>
          </div>
          <div className="col-4 float-right">
            <Buttons />
          </div>
        </div>
        <div className="row">
          <table id="pGrid" className="table table-striped">
            <thead className="thead-dark">
              <tr>
                <th onClick={() => this.requestSort('ID')} scope="col">
                  ID
                </th>
                <th scope="col">Image</th>
                <th onClick={() => this.requestSort('Title')} scope="col">
                  Product Name
                </th>
                <th scope="col">Description</th>
                <th onClick={() => this.requestSort('Quantity')} scope="col">
                  Quantity
                </th>
                <th onClick={() => this.requestSort('Price')} scope="col">
                  Price
                </th>
                <th scope="col">DELETE</th>
              </tr>
            </thead>
            <tbody>
              {this.state.product.map((data) => (
                <tr key={data.id}>
                  <td>{data.id}</td>
                  <td>
                    <img src={data.image} alt="productImage"></img>
                  </td>
                  <td className="title">{data.title}</td>
                  <td>{data.description}</td>
                  <td>{data.quantity}</td>
                  <td>${data.price}</td>
                  <td>
                    <Delete onClick={() => this.deleteProduct(data.id)} />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
};

export default ProductGrid;
