import React, { Component } from "react";
import "./style.css";
import API from "../utils/API";
import Modal from "react-bootstrap/Modal";

class Delete extends Component{
    constructor(props) {
        super(props);
        this.state ={
            deleteID: "",
            show: false
        }
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    closeModal = () => this.setState({ show: false })
    handleShow = () => this.setState({ show: true })

    refreshPage = () => {
        window.location.reload(false);
    }

    handleInputChange(event) {
        const value = event.target.value;

        this.setState({
            [event.target.name]: value
        });

    }

    handleSubmit(event) {
        event.preventDefault();
        const data = {
            id: this.state.deleteID
        }
        let show = this.state.show;
        API.deleteProduct(data, show).then((res) => {
            console.log(res);
            this.setState({ show: false });
            this.refreshPage();
        })
    }
    render() {
        return (
            <div className="absolute-center">
                <button type="button" id="delete-product" className="btn btn-danger btn-sm" onClick={this.handleShow}>
                    Delete
                </button>

            <Modal 
            show={this.state.show} 
            onHide={this.closeModal}
            aria-labelledby="ModalHeader"
        >
            <Modal.Header className="modal-header" closeButton>
                <Modal.Title id='ModalHeader'>Delete Product</Modal.Title>
            </Modal.Header>
            <form onSubmit={this.handleSubmit}>
                <Modal.Body className="modal-body">
                    <div className="row mr-1">
                        <div className="col-md-12 form-line">
                            <div className="form-group">
                                <label htmlFor="deleteID">ID of Product to be Deleted
                                    <input
                                        type="text"
                                        id="deleteID"
                                        name="deleteID"
                                        placeholder="0"
                                        value={this.state.deleteID}
                                        onChange={this.handleInputChange}/>

                                </label>
                            </div>
                            <div className="form-group">
                            <input 
                                type="checkbox" 
                                id="check" 
                                name="check" 
                                value="continue"/>
                            <label for="check">Are you sure you want to Delete</label>
                            </div>
                            </div>    
                    </div>
                </Modal.Body>
                <Modal.Footer className="modal-footer">
                    <input className="danger" onClick={this.handleSubmit} type="submit" value="Delete"/>
                </Modal.Footer>
            </form>
        </Modal>
    </div>

        )
    }

}

export default Delete;
