import React, { Component } from "react";
import "./style.css";
import API from "../utils/API";
import Buttons from "../Buttons/index";
import Delete from "../Delete/index";
import "react-bootstrap-table-next/dist/react-bootstrap-table2.min.css";
import BootstrapTable from "react-bootstrap-table-next";

const columns = [{
    dataField: "id",
    text: "ID",
    sort: true
}, {
    dataField: "image",
    text: "Image"
},{
    dataField: "title",
    text: "Product Name",
    sort: true
},{
    dataField: "description",
    text: "Description"
},{
    dataField: "quantity",
    text: "Quantity",
    sort: true
},{
    dataField: "price",
    text: "Price",
    sort: true
},];

const defaultSorted = [{
    dataField: 'name',
    order: 'desc'
  }];

class ProductGrid extends Component {
    constructor(props) {
        super(props);

        this.state = {
            product: []
        };
    }



    componentDidMount() {
       this.loadProducts();
    }    

    loadProducts = () => {
        API.getProducts()
            .then(res => {
                const products = res.data.map((data) => ({
                    image: data.image,
                    title: data.title,
                    description: data.description,
                    quantity: data.quantity,
                    price: data.price,
                    id: data.id
            }))
                this.setState({
                    product: products
                })

        });
    };

    deleteProduct = id => {
        console.log(id);
        API.deleteProduct(id)

            .then(res => this.loadProducts())
            .catch(err => console.log(err));
    };

    render() {
    return (
      <div className=" container main my-5">
        <div className="row">
            <div className="col-8">
            <h3>Products</h3>
            </div>
            <div className="col-4 float-right">
            <Buttons />
            </div>
        </div>
        <div className="row">
          <BootstrapTable keyField="id" columns= { columns } defaultSorted = { defaultSorted } className="table table-striped">
            <thead className="thead-dark">
              <tr>
                <th scope="col">ID</th> 
                <th scope="col">Image</th>
                <th scope="col">Product Name</th>
                <th scope="col">Description</th>
                <th scope="col">Quantity</th>
                <th scope="col">Price</th>
                <th scope="col">DELETE</th>
              </tr>
            </thead>
            <tbody>
                {this.state.product.map(data => 
                    <tr>
                        <td>
                            { data.id }
                        </td>
                        <td>
                            <img src={ data.image } alt="productImage"></img>
                        </td>
                        <td className="title">
                            { data.title }
                        </td>
                        <td>
                            { data.description }
                        </td>
                        <td>
                            { data.quantity }
                        </td>
                        <td>
                            ${ data.price } 
                        </td>
                        <td>
                            <Delete onClick={() => this.deleteProduct(data.id)} />
                        </td>

                    </tr>



                )}
            </tbody>
          </BootstrapTable>
        </div>
      </div>
    );
  }
}

export default ProductGrid;
